DROP TABLE IF EXISTS bookings;DROP TABLE IF EXISTS members;DROP TABLE IF EXISTS membertypes;DROP TABLE IF EXISTS classes;CREATE TABLE membertypes(	id SERIAL PRIMARY KEY,	name VARCHAR(255) NOT NULL);CREATE TABLE members(	id SERIAL PRIMARY KEY,	name VARCHAR(255) NOT NULL,	date_of_birth DATE NOT NULL,	membertype_id INT REFERENCES membertypes(id));CREATE TABLE classes(	id SERIAL PRIMARY KEY,	name VARCHAR(255) NOT NULL,	event_time TIMESTAMP NOT NULL,	capacity INT NOT NULL);CREATE TABLE bookings(	id SERIAL PRIMARY KEY,	member_id INT REFERENCES members(id) ON DELETE CASCADE,	class_id INT REFERENCES classes(id) ON DELETE CASCADE);-- \dt-- select * from membertype;-- select * from members;-- select * from classes;-- select * from bookings;-- show all members alongside their bookings (even if they haven't made any)-- SELECT * FROM bookings right JOIN members ON members.id = bookings.member_id;-- SELECT * FROM members left JOIN bookings ON members.id = bookings.member_id;	 same resultSELECT *FROM membersJOIN bookings    ON members.id = bookings.member_idJOIN classes    ON classes.id = bookings.class_idWHERE members.id = 1ORDER BY event_time;-- SELECT *FROM classesJOIN bookings    ON classes.id = bookings.class_idJOIN members    ON members.id = bookings.member_idWHERE classes.id = 26ORDER BY members.name;-- -- SELECT members.*-- FROM members-- JOIN membertypes--     ON membertypes.id = members.membertype_id-- WHERE members.membertype_id = 1-- ;